let Vec3 = struct {
    x: f32,
    y: f32,
    z: f32,
};

let add_vector = (a: Vec3, b: Vec3) -> Vec3 {
    return Vec3 {
        x: a.x + b.x,
        y: a.y + b.y,
        z: a.z + b.z,
    };
}

